import { useCustomScroll } from "../../../composition/useCustomScroll";
import { onMounted, reactive, computed, ref, onUpdated } from "vue";
import ModalIntegrationBitrix from "../../Modals/integrations/ModalIntegrationBitrix";
import ModalIntegrationAmoCRM from "../../Modals/integrations/ModalIntegrationAmoCRM";
import ModalIntegrationYclients from "../../Modals/integrations/ModalIntegrationYclients";
import BaseButton from "../../Base/BaseButton";
import SettingsIntegrationsCard from "./SettingsIntegrationsCard/SettingsIntegrationsCard";
import {integrationCards} from "./SettingsIntegrationsCard/settings-integrations-card";
import {useIntegrations} from "../../../composition/useIntegrations";
import {integrationHeader} from "../../../views/settings/integrations/integrationHeader/integrationHeader.js";
export default {
  components: {
    ModalIntegrationBitrix,
    ModalIntegrationAmoCRM,
    ModalIntegrationYclients,
    BaseButton,
    SettingsIntegrationsCard,
  },
  props: {
    bitrixProps: Object,
    amoProps: Object,
    yclientsProps: Object,
    activeLink: Boolean,
    openList: Boolean,
refreshAmo:Boolean,
  },
  setup(props, { emit }) {
    const {activateForm, isAmoClick} = integrationHeader();
    const { container, content, scrollbar, scrollTo, init } = useCustomScroll();
     const { connectAmocrm, connectBitrix,connectYclients  } = useIntegrations()
    const cardImg = ref([]);
    const checkactiveLink = () => {

    }

    onUpdated(()=>{
      checkactiveLink();
    })
   
    const { checkActiveCard, activeTabs, activeAmoCrmPage} = integrationCards()
    cardImg.value = ["data:image/png;base64,",
    "data:image/png;base64,"];

    

    onMounted(() => {
      init();
 
    });
const pastAuth = (login, pass) => {

  const log = login.value;
  const password = pass.value
  const data = {log, password}
  yclients.connect(data)
}
const updateAmoHeader = () => {
  if(activeAmoCrmPage.value){
  }
  else{
  }
}
    const bitrix = reactive({
      action: () => {
        if (props.bitrixProps.is_active) {
          emit("openForm", "bitrix");
          //open form
        } else {
          bitrix.toggleOpened(true);
        }
      },
      isOpened: false,
      toggleOpened: (boolean) => (bitrix.isOpened = boolean),
      connect: (url) => {
        connectBitrix(url).then((r) => {
          emit("getBitrix");
          bitrix.toggleOpened(false);
        });
      },
    });
const linkForm = ref(false)
const propsActive = () => {
 
  if(isAmoClick.value){

    linkForm.value=true
     return true
  }
 else{

    linkForm.value=false
      return false
 }
}
    const yclients = reactive({
      action: () => {
        if (props.yclientsProps.is_active) {
          emit("openForm", "yclients");
          //open form
        } else {
          yclients.toggleOpened(true);
        }
         emit("redirectYclient");
      },
      isOpened: false,
      toggleOpened: (boolean) => (yclients.isOpened = boolean),
      connect: (data) => {
        connectYclients(data).then((r) => {
          emit("getYclients");
          yclients.toggleOpened(false);
        });
      },
      del: () => {
         emit("deleteYclient");
      }
    });
    checkActiveCard(false);
    const amo = reactive({
      action: () => {
        if (props.amoProps.is_active) {
          emit("openForm", "amo");
          checkActiveCard(true)
          activeTabs(true)
          //open form
        } else {
          amo.toggleOpened(true);
          activeTabs(false)
           checkActiveCard(false)
        }
      },
      isOpened: false,
      toggleOpened: (boolean) => (amo.isOpened = boolean),
      connect: () => {
        connectAmocrm();
      },
      del: () => {
         emit("deleteAmo");
      }
    });
    const ff = () => {
  
    }
const openModel = (name) => {
      name.toggleOpened(true);
}

const linkSettings = () => {

    //  amo.action();
    emit("openForm", "amo");
    emit("refreshAmo");
      checkActiveCard(true)
          activeTabs(true)
           amo.toggleOpened(false);


}
const linkDefault = () =>{


  // amo.action()
  //     amo.toggleOpened(false);
}

    return {
      container,
      content,
      scrollbar,
      scrollTo,
      amoProps: computed(() => props.amoProps),
      bitrixProps: computed(() => props.bitrixProps),
      yclientsProps: computed(() => props.yclientsProps),
      cardImg,
      bitrix,
      amo,
      yclients,
      checkActiveCard,
  linkSettings,
      ff,
      activateForm,
      isAmoClick,
      propsActive,
      linkForm,
      linkDefault,
      openModel,
      pastAuth,
      activeAmoCrmPage,
      updateAmoHeader
    };
  },
};
