<script>
    import { useCustomScroll } from "../../../composition/useCustomScroll";
    import { useUserInfo } from "../../../composition/useUserInfo";
    import { useAuth } from "../../../composition/useAuth";
    import BaseCircleIcon from "../../Base/BaseCircleIcon";
    import BaseFolder from "../../Base/BaseFolder";
    import SlideUpDown from 'vue3-slide-up-down';
    import {inject, computed, onMounted, ref, watch} from "vue";
    import {useDialogs} from "../../../composition/useDialogs";
    import {useFolder} from "../../../composition/useFolder";
    import {useContextMenu} from "../../../composition/useContextMenu";
    import {useModals} from "../../../composition/useModals";
    import {useLoader} from "../../../composition/useLoader";
    import {useUser} from "../../../composition/useUser";
    import {MessengerContentInput} from "../../../components/MessengerContent/MessengerContentNav/messenger-content-nav.js";
    export default {
        components: {SlideUpDown, BaseCircleIcon, BaseFolder},
        props: {},
        setup(props, {emit}) {
            const { container, content, scrollbar, scrollTo, init } = useCustomScroll()
             const { outAuth } = useAuth();
            const activeDialogs = ref(false)
            const activeSettings = ref(false);
            const openedAccounts = ref(false);
            const activeMailing = ref(false);
            const settings = ref([
                ['Каналы', '/settings/whatsapps'], 
                ['Интеграции', '/settings/integrations'],
                // ['Настройки аккаунта', '/settings/account'],
                // ['Статитистика','/'],
                // ['Открытое API','/'],
                // ['Шаблоны сообщений', '/settings/message-templates'],
                // ['Управление менеджерами', '/'],
                ['Игнор лист', '/settings/ignore'],
                // ['Валидация базы', '/'],
            ])
            const { getFolder, activeFolder } = MessengerContentInput()
            const { getDialogs, selectedGroupDialogs, toggleAllSelectedGroupDialogs } = useDialogs()
            const { selectedFolder, folders, selectFolder, getAllFoldersInFolder, selectedParentFolder, foldersInSelectedFolder, selectParentFolder, getAllFolders } = useFolder()
            const { toggleModalEditFolders } = useModals()
            const { setContext } = useContextMenu()
            const { toggleOpenedUserInfo, openedUserInfo } = useUserInfo()
            const { isLoadingDialogs } = useLoader()
            const { user } = useUser()
           
            const openContextMenu = ($event, context) => {
                setContext({
                    top: $event.clientY,
                    left: $event.clientX,
                }, context)
            }
            const choseFolder = (id) => {
                if (selectedFolder.value !== id) {
                    selectFolder(id); 
                    isLoadingDialogs.value = true;
                    getAllFoldersInFolder(id, true);
                    getDialogs(id)
                        .then((r) => {
                       
                            isLoadingDialogs.value = false;
                            const isGetDialog = ref([])
                            const foldersNew = ref([]);
                            foldersNew.value = folders.value
                            if(  foldersNew.value.filter(i=>i.folder_id==selectedFolder.value)){
                                isGetDialog.value = foldersNew.value.filter(i=>i.folder_id==selectedFolder.value);
                                   if(r){
                                    emit("getDialogs")
                                } 
                            }
                             else{
                                  isGetDialog.value = foldersNew.value.folders.filter(i=>i.folder_id=selectedFolder.value);
                                     if(r){
                                    emit("getDialogs")
                                } 
                             }
                          
                        })
                }
                toggleAllSelectedGroupDialogs(false);
                toggleOpenedUserInfo(false);
                getFolder(selectedFolder.value)
            }
            const parentFolderName = computed(() => {
                return folders.value.find(i => i.folder_id === selectedParentFolder.value).name
            })
            const isGetFolder = ref(false);
            const checkLink = () => {
                isGetFolder.value = true;
                getAllFolders()
                .then((r)=>{
                     const href = window.location.pathname;
                if ( href == '/settings/mailings'){
                    activeMailing.value = true;
                    selectedFolder.value = null
                }
                if ( href == '/messenger'){
                    activeDialogs.value = true;
                }
                if (href.startsWith('/settings/')){
                    selectedFolder.value = null
                    const elem = document.querySelector('.router-link-active')
                    const parent = elem.parentNode.classList[2] ;
                    if(parent=='settings-nav__link-list-slider_settings'){
                        activeSettings.value=true;
                         
                         
                    }
                }  
             
                })
                ; 
           
            }
            const pathName = ref('null')
            const getPath =() => {
                    const hrefNew = document.location.pathname;
                    if(hrefNew.includes('/settings/finance')){
                        pathName.value = 'finance'
                    }
                     else if(hrefNew.includes('/settings/partners')){
                        pathName.value = 'partners'
                    }
                    else{
                         pathName.value = 'null'
                    } 
            }
            const pathNamePost = (name) => {
                    if(name === 'partners'){
                       pathName.value = 'partners' 
                    }
                   else if(name === 'finance'){
                         pathName.value = 'finance' 
                    }
                    else{
                        pathName.value = '' 
                    }
              
            }
            const checkFolderChildren = () => {
                for(let i = 0; i< folders.value.length; i++){
                    if(folders.value[i].folders){
                        const folder =ref()
                        folder.value =  folders.value[i].folders.find(item => item.folder_id==selectedFolder.value);
                        if(folder.value){
                            const folderParent = folders.value.find(elem => elem.folder_id ==folder.value.parent_folder_id)
                            return folderParent.folder_id
                        }
                        
                    }
                    
                }
            }
            watch(()=>{
                if(folders.value){
                    checkFolderChildren();
                }
                if(activeDialogs.value){
                     activeSettings.value=false
                }
                else if (activeSettings.value) {
                    activeDialogs.value=false
                }
            })
            onMounted( () => {
                init()
                checkLink();
                getPath()
                
            
            })
            
            return {
                container,
                content,
                scrollbar,
                scrollTo,
                openedAccounts,
                toggleOpenedUserInfo,
                activeDialogs,
                activeSettings,
                settings,
                activeMailing,
                outAuth,
                container,
                content,
                scrollbar,
                scrollTo,
                choseFolder,
                toggleModalEditFolders,
                openContextMenu,
                toggleOpenedUserInfo,
                openedUserInfo,
                image: computed(() => user.value.avatar),
                userName: computed(() => user.value.login),
                folders,
                foldersInSelectedFolder,
                parentFolderName,
                selectedFolder,
                selectParentFolder,
                selectedParentFolder,
                selectedGroupDialogs,
                getAllFolders,
                checkLink,
                isGetFolder,
                checkFolderChildren,
                getPath,
                pathName,
                pathNamePost
            }
        }
    }
</script>



