<script>
import { ref, onUpdated, onMounted } from 'vue'
import { usePartners } from "@/composition/usePartners";
export default {
    props: {
        partner: Object,
    },
    emits:["deleteLink"],
    setup(props, {emit}){

        const {partners ,getPartners, deletePartners} = usePartners()

        const tableCells = ref(
        {link: "https://fastlead.app?referral="},
        )

        const openModalChange = () => {
            emit('openModalChange');
        }

        const deleteLink = () => {
            emit('deleteLink', props.partner.affiliate_id);
        }

        const copyLink = () => {
            let link = tableCells.value.link+partner.value.url
        }

        return{
            tableCells,

            openModalChange,
            partners,

            deleteLink,
            copyLink,

        }
    }
}
