<script>
    import BaseSwitcher from "../../Base/BaseSwitcher";
    import { useStyle } from "../../../composition/useStyle";
    import { useModals } from "../../../composition/useModals";
    import { useAuth } from "../../../composition/useAuth";

    export default {
        components: { BaseSwitcher },
        setup() {
            const { style, setStyle } = useStyle();

            const { toggleModalEditFolders } = useModals();

            const { outAuth } = useAuth();

            return {
                style,
                setStyle,
                toggleModalEditFolders,
                outAuth
            }
        }
    }
</script>

<style lang="scss">
    .user-info__settings {
        margin-top: 17px;
    }
    .user-info__setting-name {
        font-style: normal;
        font-weight: normal;
        font-size: 18px;
        line-height: 24px;
        color: var(--user-info-settings-default-setting-name);
        margin-left: 10px;
        text-decoration: none;
        transition: .2s ease;
    }
    .user-info__setting {
        display: flex;
        align-items: center;
        cursor: pointer;
        padding: 11px 17px;
        text-decoration: none;
        transition: .2s ease;
        .user-info__setting-icon{
            min-width: 18px;
            path {
                transition: fill .2s ease;
                fill: var(--user-info-settings-default-svg-fill);
            }
        }
        &:hover {
            background: var(--user-info-settings-hover-setting-bg);
            .user-info__setting-name {
                color: var(--user-info-settings-hover-svg-fill);
            }
            .user-info__setting-icon path {
                fill: var(--user-info-settings-hover-svg-fill);
            }
        }
    }

    .user-info__setting-name_flex {
        display: flex;
        width: 100%;
        justify-content: space-between;
        align-items: center;
    }
</style>