<script>
import {ref, onMounted} from "vue"

export default {
    props:{
        data:Object,
        phones:Array,
        search:Array,
    },
    emits:["edit"],
    setup(props, {emit}) {
        const edit = (phone) => {
            emit("edit", )
            emit("getPhone", phone)
        }
        const del = (phone) => {
             emit("del" )
             emit("del", phone)
        }
        const arrNumber = ref();
            onMounted(()=>{
            })
        const formatedPhone = (number) => {
            let phone = number;
            if(phone.match(/^\d+$/)){
                let match = phone.match(/^(\d{1})(\d{3})(\d{3})(\d{2})(\d{2})$/);
                const num ='+'+ match[1] + ' (' + match[2]+ ') '+ match[3] + '-'+ match[4]+ '-'+match[5];
                phone =num;
            }
            return phone;
            }
        return {
            edit,
            arrNumber,
            formatedPhone,
            del
        }
    },
}
</script>
